    let subType = localStorage.getItem("subType");
    // const { email, app, type, subType } = useParams();
    const context = React.useContext(Context);
    const {
        tabsConfig,
        selectedTab,
        refreshTable,
        openDownloadTab,
        updateState,
        currencyImageList,
