import mcbBrand from "../../static/images/globaldrawer/mcb_brand.svg";
import mcbApp from "../../static/images/globaldrawer/mcb_app.svg";
import mcbConglomerate from "../../static/images/globaldrawer/mcb_conglomerate.svg";
