                            <option value={item?.name}>{item?.name}</option>
                          ))}
                      
                    </select>
                      {/* <input
                        type="text"
                        placeholder="Country.."
                        value={pathSteps?.country}
                        onChange={(e) => {
                          setPathSteps((prev) => {
                            return {
                              ...prev,
                              country: e.target.value,
                            };
                          });
                        }}
                      /> */}
                    </div>
                  </div>

                  <div className="each-acc-addpath-field">
                    <div className="each-acc-addpath-field-name">
                      What personality suits this path?
                    </div>
                    <div className="optionCardFullWrapper">
                      {personalityList.map((item) => (
                        <div
                          className={
                            item === personality
                              ? "optionCardFullSelected"
                              : "optionCardFull"
                          }
                          onClick={(e) => handlePersonality(item)}
                        >
                          {item}
                        </div>
                      ))}
                    </div>
                  </div>

                  <div className="each-acc-addpath-field">
                    <div
                      className="submit-path-btn"
                      style={{
                        opacity: creatingPath
                          ? "0.5"
                          : pathSteps?.nameOfPath &&
                            pathSteps?.description &&
                            pathSteps?.length &&
                            pathSteps?.path_type &&
                            // pathSteps?.the_ids?.length > 0 &&
                            pathSteps?.destination_institution &&
                            pathSteps?.program &&
                            pathSteps?.city &&
                            pathSteps?.country &&
                            grade.length > 0 &&
                            gradeAvg.length > 0 &&
                            curriculum.length > 0 &&
                            stream.length > 0 &&
                            finance.length > 0 &&
                            personality !== ""
                          ? "1"
                          : "0.5",
                        cursor: creatingPath
                          ? "not-allowed"
                          : pathSteps?.nameOfPath &&
                            pathSteps?.description &&
                            pathSteps?.length &&
                            pathSteps?.path_type &&
                            // pathSteps?.the_ids?.length > 0 &&
                            pathSteps?.destination_institution &&
                            pathSteps?.program &&
                            pathSteps?.city &&
                            pathSteps?.country &&
                            grade.length > 0 &&
                            gradeAvg.length > 0 &&
                            curriculum.length > 0 &&
                            stream.length > 0 &&
                            finance.length > 0 &&
                            personality !== ""
                          ? "pointer"
                          : "not-allowed",
                      }}
                      onClick={() => {
                        if (
                          pathSteps?.nameOfPath &&
                          pathSteps?.description &&
                          pathSteps?.length &&
                          pathSteps?.path_type &&
                          // pathSteps?.the_ids?.length > 0 &&
                          pathSteps?.destination_institution &&
                          pathSteps?.program &&
                          pathSteps?.city &&
                          pathSteps?.country &&
                          grade.length > 0 &&
                          gradeAvg.length > 0 &&
                          curriculum.length > 0 &&
                          stream.length > 0 &&
                          finance.length > 0 &&
                          personality !== ""
                        ) {
                          pathSubmission();
                        }
                      }}
                    >
                      {creatingPath ? "Loading.." : "Submit Path"}
                    </div>
                    <div
                      className="go-back-btn"
                      onClick={() => {
                        setpstep(1);
                        setPathSteps({
                          nameOfPath: "",
                          description: "",
                          length: "",
                          path_type: "",
                          the_ids: [],
                          destination_institution: "",
                        });
                        setGrade([]);
                        setGradeAvg([]);
                        setCurriculum([]);
                        setStream([]);
                        setFinance([]);
                        setPersonality("");
                      }}
                    >
                      Go Back
                    </div>
                  </div>
                </div>
              ) : pstep === 9 ? (
                <NewStep1 setpstep={setpstep} />
              ) : pstep === 10 ? (
                <div>
                  <div className="acc-step-text">Bulk Path Action</div>
                  <div>
                    <div
                      className="acc-step-box"
                     
                      style={{
                        background:
                          billingType === "Download"
                            ? "#182542"
                            : "",
                        color:
                          billingType === "Download" ? "#FFF" : "",
                      }}
                      onClick={e => handleDownload('Path')}
                    >
                      Download
                      
                    </div>
                    <div
                      className="acc-step-box"
                      onClick={handleImageClick}
                      style={{
                        background: billingType === "Upload" ? "#182542" : "",
                        color: billingType === "Upload" ? "#FFF" : "",
                      }}
                    >
                      Upload
                      <input
                        type="file"
                        // accept=".xlsx, .xls, .gsheet, .numbers, application/vnd.google-apps.spreadsheet, application/vnd.apple.numbers"
                        onChange={handleFileInputChange1}
                        style={{ display: "none" }}
                        ref={fileInputRef}
                      />

                    </div>
                    
                  </div>
                  <div
                    className="goBack"
                    onClick={() => {
                      setpstep(1);
                      // setbillingType("");
                    }}
                  >
                    Go Back
                  </div>
                </div>
              ) : pstep === 11 ? (
                <div>
                  <div className="acc-step-text">Bulk Step Action</div>
                  <div>
                    <div
                      className="acc-step-box"
                      onClick={e => handleDownload('Step')}
                      style={{
                        background:
                          billingType === "Download"
                            ? "#182542"
                            : "",
                        color:
                          billingType === "Download" ? "#FFF" : "",
                      }}
                     
                    >
                      Download
                      
                    </div>
                    <div
                      className="acc-step-box"
                      onClick={handleImageClick}
                      style={{
                        background: billingType === "Upload" ? "#182542" : "",
                        color: billingType === "Upload" ? "#FFF" : "",
                      }}
                    >
                      Upload
                      <input
                        type="file"
                        // accept=".xlsx, .xls, .gsheet, .numbers, application/vnd.google-apps.spreadsheet, application/vnd.apple.numbers"
                        onChange={handleFileInputChange2}
                        style={{ display: "none" }}
                        ref={fileInputRef}
                      />

                    </div>
                    
                  </div>
                  <div
                    className="goBack"
                    onClick={() => {
                      setpstep(1);
                      // setbillingType("");
                    }}
                  >
                    Go Back
                  </div>
                </div>
              ) : pstep === 12 ? (
                <div>
                  <div className="acc-step-text">Uploaded Successfully</div>
                
                  <div
                    className="goBack"
                    onClick={() => {
                      setpstep(1);
                      setbillingType("");
                    }}
                  >
                    Go Back
                  </div>
                </div>
              ): pstep === 13 ? (
                <div>
                  <div className="acc-step-text">Bulk Service Action</div>
                  <div>
                    <div
                      className="acc-step-box"
                     
                      style={{
                        background:
                          billingType === "Download"
                            ? "#182542"
                            : "",
                        color:
                          billingType === "Download" ? "#FFF" : "",
                      }}
                      onClick={e => handleDownload('Service')}
                    >
                      Download
                      
                    </div>
                    <div
                      className="acc-step-box"
                      onClick={handleImageClick}
                      style={{
                        background: billingType === "Upload" ? "#182542" : "",
                        color: billingType === "Upload" ? "#FFF" : "",
                      }}
                    >
                      Upload
                      <input
                        type="file"
                        // accept=".xlsx, .xls, .gsheet, .numbers, application/vnd.google-apps.spreadsheet, application/vnd.apple.numbers"
                        onChange={handleFileInputChange3}
                        style={{ display: "none" }}
                        ref={fileInputRef}
                      />

                    </div>
                    
                  </div>
                  <div
                    className="goBack"
                    onClick={() => {
                      setpstep(1);
                      // setbillingType("");
                    }}
                  >
                    Go Back
                  </div>
                </div>
              ):(
                ""
              )}
            </>
          </div>
        ) : (
          ""
        )}
      </>



      <>
        {coinActionEnabled && (
          <div className="acc-popular" onMouseDown={(e) => e.stopPropagation()}>
            <div
              className="acc-popular-top"
              style={{ height: "3rem", marginBottom: "0" }}
            >
              <div className="acc-popular-head">
                {selectedCoin?.coinName} Actions
              </div>
              <div
                className="acc-popular-img-box"
                onClick={() => resetCoinAction()}
                style={{ cursor: "pointer" }}
              >
                <img className="acc-popular-img" src={closepop} alt="" />
              </div>
            </div>
            <>
              {coinAction.includes("Menu") ? (
                <div>
                  <div className="acc-step-text1">
                    What would you like to do?
                  </div>
