                  onClick={() => {
                    setServiceActionStep(2);
                  }}
                >
                  Delete Service
                </div>
                <div
                  className="each-action1"
                  onClick={() => {
                    setServiceActionStep(4);
                  }}
                >
                  Edit Service
                </div>
