          width: "500px",
          overflowY: "scroll",
          background: "white",
          borderLeft: "1px solid #e7e7e7",
        }}
      >
        <>
          <div className="headTabBox">
            <div
              className={`tab ${tab === "Filter"}`}
              onClick={() => {
                setTab("Filter");
                setStep("");
              }}
            >
              Filter
            </div>
            <div
              className={`tab ${tab === "API"}`}
              onClick={() => {
                setTab("API");
                setStep("");
              }}
            >
              API
            </div>
          </div>
          <div className="header">
            <div className="content">
              <div className="title">Bond Offerings</div>
              <div className="breadCrumbs">
                <span
                  onClick={() => {
                    setStep("");
                    setSubStep("");
                  }}
                >
                  {tab}
                </span>{" "}
                {step ? (
                  <>
                    -&gt;&nbsp;
                    <span
                      onClick={() => {
                        setSubStep("");
                      }}
                    >
                      {step}
                    </span>
                  </>
                ) : (
                  ""
                )}
                {subStep ? (
                  <>
                    -&gt;&nbsp;
                    <span>{subStep}</span>
                  </>
                ) : (
                  ""
                )}
              </div>
            </div>

            <div
              className="backBtn"
              onClick={() => {
                if (subStep) {
                  setSubStep("");
                } else {
                  setStep("");
                }
              }}
            >
              <img src={back} alt="" />
            </div>

            <div
              className="backBtn"
              onClick={() => {
                setGlobalFilter(false);
              }}
            >
              <img src={close} alt="" />
            </div>
          </div>
        </>
        {getContent()}
      </div> */}
    </>
  );
};

export default GlobalFilterDrawer;
