    GetAllSpecialties().then((res) => {
      if (res.data.status) {
        setSpeaclities(res.data);
      }
    });
  };

  const handleAccountant = () => {
    let userEmail = userDetails?.user?.email;
    GetAllAccountants(userEmail).then((res) => {
      if (res.data.status) {
        // console.log(res?.data, 'res.data');
        setAccountantsList(res.data);
      }
    });
  };

