      )
      .then((response) => {
        let result = response?.data;
        // console.log(result, "check for preLogin result");
        if (result?.status) {
          setCheckForHistory(true);
          getPathDetails(result?.data[0]?.pathId);
        } else {
          setCheckForHistory(false);
        }
      })
      .catch((error) => {
