                {/* <img
                src={editPub}
                alt=""
                className="coin"
                style={{ width: "25px", height: "25px" }}
              /> */}
                <div>Edit</div>
              </div>
              <div
                className="sidebarCard"
                onClick={() => {
                  setStep("Delete Publication");
                  setPath([...path, "Delete Publication"]);
